//////////
// initLessonCombo
function initLessonCombo(){
    let first_title = lessons[0].title;

    //lessons.sort(function(a,b){return a.lev>b.lev?1:(a.lev<b.lev?-1:(a.title>b.title?1:(a.title<b.title?-1:0)))});
    // multy sorting
    let sorts = [
            function(a, b){return a.lev>b.lev ? 1: (a.lev<b.lev ? -1 : 0)},	// byLev
            function(a, b){return a.title>b.title ? 1 : (a.title<b.title ? -1 : 0)}, // byTitle
            // function(a, b){return a.title[0]>b.title[0] ? 1 : (a.title[0]<b.title[0] ? -1 : 0)}, // byFirstTitle
            //function(a, b){return a.en.length>b.en.length ? 1 : (a.en.length<b.en.length ? -1 : 0)}, // byEnLength
            //function(a, b){return a.en.length>b.en.length ? 1 : (a.en.length<b.en.length ? -1 : 0)},
        ],
        sortFoo = function(){return 0};
    for(let i=sorts.length-1;i>-1;i--){
        sortFoo = (function (foo, _sortFoo) {
            return function(a,b){
                return foo(a,b) || _sortFoo(a,b);
            }
        }(sorts[i], sortFoo));
    }
    lessons.sort(sortFoo);

    let i, levgroup='', initInd = 0,
        newOption = function(text, value, defaultSelected, selected, attrs){
            let op = new Option(text, value, defaultSelected, selected);
            attrs = attrs || {};
            for(let k in attrs)
                op.setAttribute(k, attrs[k]);
            return op;
        },
        select = document.getElementById('lessonCombo');
    for(i=0;i<lessons.length;i++){
        let les = lessons[i],
            opt = newOption(les.title, i,  false, false, {'data-level': les.lev}); // +' ('+les.lev+')'
        // pseudo GROUP block
        if(levgroup!==les.lev){
            levgroup=les.lev;
            let gr = newOption('*** Level '+les.lev+': ***', '',  false, false, {'disabled':'disabled'});
            select.appendChild(gr);
        }
        select.appendChild(opt);
        if(les.title === first_title) initInd = i;
    }
    let sel = newOption('...', -1,  false, false, {'disabled':'disabled'});
    select.appendChild(sel);

    select.value = initInd; // first or
};